'use strict';

const Keys = require('@hapi/validate/lib/types/keys');


const internals = {};


module.exports = Keys._extend({

    type: 'object',

    cast: {
        map: {
            from: (value) => value && typeof value === 'object',
            to(value, helpers) {

                return new Map(Object.entries(value));
            }
        }
    }
});
